import React, {Component} from 'react';
import Cover from './Cover';
import Loader from './Loader';

class Modal extends Component {

    render() {
        const {click, titleName, artistName, albumName, genre, length, rating, urlList, cover} = this.props;
        return (
            <aside className="modal">
                <div className="modal-wrapper">
                    <button onClick={click} className="modal-close" value="Fermer la modal">X</button>
                    <p>Titre : {titleName}</p>
                    <p>Artistes : {artistName}</p>
                    <p>Albums : {albumName}</p>
                    <p>Genre : {genre}</p>
                    <p>Dur√©e : {length}</p>
                    <p>Note : {rating}</p>
                    <p>Covers : </p>
                    <div>
                        {urlList.map((u,i) => <Cover key={i} {...u}/>)}
                        <Loader/>
                    </div>
                    {!cover && <p>Pas de cover disponible</p>}
                </div>
            </aside>  
        )
    }
}

export default Modal;
