import React, {Component} from 'react';
import Artist from "../components/Artist";

class ResultsTable extends Component {

    constructor(props){
        super(props);
        this.state = {
          "artistsList" : [],
        }
      };

      addartists = (artists) => {
        const {artistsList} = this.state;

        artists.map(a => 
            artistsList.push({
            "name": a.name,
        }));

        this.setState({
            "artistsList": artistsList,
        })
    };


    render() {
        const {artistsList} = this.state;
        return (
            <div>
                <thead>
                    <tr>
                    <th>#</th>
                    <th>Artiste</th>
                    <th>Titre</th>
                    <th>Album</th>
                    <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                    <th>1</th>
                    <th>{artistsList.map((t,i) => <Artist key={i} {...t} />)}e</th>
                    <th>divers titres</th>
                    <th>Album</th>
                    <th><input type="submit" value="Voir"/></th>
                    </tr>
                </tbody>
            </div>
        )
    }
}

export default ResultsTable;
