import React, {Component} from 'react';
import './App.css';
import SearchForm from './components/SearchForm';
import Modal from './components/Modal';
import Response from './components/Response';
import ResultsTable from './components/ResultsTable';
import {sendSearch} from './api/musicBrainzAPI'; 
import {sendSearchDetails} from './api/musicBrainzAPI'; 


class App extends Component {

  constructor(props){
      super(props);
      this.state = {
        "responsesList" : [],
        "addSearch": "",
        "addSelect": "",
        'makeSearch' : true,
        'nbResult': "",
        'emptyResult': false,
        'visuResult': false,
        'seeMore': false,
        'visualModal': true,
        "responseDetail" : [],
      }
      this.offset = 0;
    };

  submit = (ev) => {
      ev.preventDefault();

      this.setState({
          "responsesList": [],
          "addSearch": "",
          "addSelect": "",
      })
      this.offset = 0;

      const {addSearch, addSelect} = ev.target;

      this.setState({
          "addSearch": addSearch.value,
          "addSelect": addSelect.value,
      })

      sendSearch(addSelect.value, addSearch.value, this.offset, this.addResponse);
  };

  clickSeeMore = () => {
      const {addSearch, addSelect} = this.state;
      this.offset += 100;
      sendSearch(addSelect, addSearch, this.offset, this.addResponse);

  };

  addResponse = (response) => {
      const {responsesList} = this.state;

      response.recordings.map(a => 
          responsesList.push({
              "artistName":a["artist-credit"]?.[0].name,
              "titleName": a?.title,
              "albumName": a["releases"]?.[0].title,
              "idRecording": a?.id,
      }));

      this.setState({
          "responsesList": responsesList,
      })

      // this.props.test(responsesList);

      //pour gérer l'apparission ou non du bouton "voir plus"
      if(this.offset+100 < response.count){
          this.setState({
              "seeMore": true,
          });
      }else{
          this.setState({
              "seeMore": false,
          }); 
      } 

      //pour gérer l'apparission ou non du tableau des résultats ou d'un texte
      if(response.count > 0){
        this.setState({
          "visuResult": true,
          "makeSearch": false,
          "emptyResult": false,
          "nbResult" : response.count,
        }); 
      }else{
        this.setState({
          "visuResult": false,
          "makeSearch": false,
          "emptyResult": true,
        }); 
      }

  };

  clickOnModal = () => {
    const {visualModal} = this.state;
    this.setState({
      "visualModal": !visualModal,
    }); 

    const id = "54f6a421-470e-4b22-b2d6-9b688eac8313"

    sendSearchDetails(id, this.addDetails);
  };

  addDetails = (response) => {
    const {responseDetail} = this.state;

    let $artists = [];
    for(let i of response["artist-credit"]){
      $artists = response["artist-credit"]?.[i].name
    }
    console.log(artists);
    responseDetail.push({
        "titleName": response?.title,
        "artistName": $artists,
        "albumName": response["releases"]?.[0].title,
        "genre": response?.genres,
        "length": response?.length,
        "rating": response["rating"]?.value,
      });

      this.setState({
          "responseDetail": responseDetail,
      })
  };

  addCover = (responseCover) => {
  };

  clickOffModal = () => {
    const {visualModal} = this.state;
    this.setState({
      "visualModal": !visualModal,
    }); 
  };

  render() {
      const {responsesList, seeMore, visualModal, visuResult, makeSearch, nbResult, emptyResult, responseDetail} = this.state;
      return (
          <div className="App">
             <SearchForm submit={this.submit}/> 
              {/* <form onSubmit={this.submit}>
                      <label >Votre recherche : </label>
                      <input type="text" placeholder="Votre recherche par Artiste, Album, Titre ou dans tout" name="addSearch" />
                      <select name="addSelect">
                          <option value="everything">Tout</option>
                          <option value="artist">Artiste</option>
                          <option value="release">Album</option>
                          <option value="recording">Titre</option>
                      </select>
                      <input type="submit" value="Rechercher"/>
              </form> */}

              {visuResult && <p>{nbResult} résultats</p>}

              {visuResult && <ResultsTable result={responsesList.map((r,i) => <Response key={i} {...r} numRslt={i+1} click={this.clickOnModal}/>)}/> }

              {makeSearch && <p>Veuillez effectuer une recherche</p>}

              {emptyResult && <p>Aucun résultat pour votre recherche</p>}

              {seeMore && <button onClick={this.clickSeeMore}>Voir plus</button> }
              {/* <aside className="modal" hidden={visualModal}>
                <div className="modal-wrapper">
                    <button onClick={this.clickModal} className="modal-close" id="modal-close" value="Fermer la modal">X</button>
                    <p>test</p>
                    <p>test</p>
                </div>
              </aside>   */}
              {responseDetail.map((d,i) => <Modal key={i} {...d} visual={visualModal} click={this.clickOffModal}/>)}
              {/* <Modal visual={visualModal} click={this.clickOffModal}/>  */}
          </div>
      )
  }
}

export default App;
