import React, {Component} from 'react';
import {sendSearchArtist} from '../api/musicBrainzAPI'; 
import {sendSearchAlbum} from '../api/musicBrainzAPI'; 
import {sendSearchTitle} from '../api/musicBrainzAPI'; 
import Artist from './Artist';
import Album from './Album';
import ResultsTable from './ResultsTable';

class SearchForm extends Component {

    constructor(props){
        super(props);
        this.state = {
          "artistsList" : [],
          "albumsList" : [],
        }
      };

    submit = (ev) => {
        ev.preventDefault();
        const {addSearch, addSelect} = ev.target;
        if(addSelect.value === "artist"){
            sendSearchArtist(addSearch.value,this.addArtists);
        }else if(addSelect.value === "album"){
            sendSearchAlbum(addSearch.value,this.addAlbums);
        }else if(addSelect.value === "title"){
            sendSearchTitle(addSearch.value);
        }else {
            //pour everything
        }

    };

    addArtists = (artists) => {
        const {artistsList} = this.state;

        artists.artists.map(a => 
            artistsList.push({
            "name": a.name,
        }));

        this.setState({
            "artistsList": artistsList,
        })
    };

    addAlbums = (albums) => {
        const {albumsList} = this.state;

        albums.releases.map(a => 
            albumsList.push({
            "album": a.title,
        }));

        this.setState({
            "albumsList": albumsList,
        })
    };

    render() {
        const {artistsList, albumsList} = this.state;
        return (
            <div>
                <form onSubmit={this.submit}>
                        <label >Votre recherche : </label>
                        <input type="text" placeholder="Votre recherche par Artiste, Album, Titre ou dans tout" name="addSearch" />
                        <select name="addSelect">
                            <option value="everything">Tout</option>
                            <option value="artist">Artiste</option>
                            <option value="album">Album</option>
                            <option value="title">Titre</option>
                        </select>
                        <input type="submit" value="Rechercher"/>
                </form>

                <div>
                    <ResultsTable result={artistsList.map((a,i) => <Artist key={i} {...a} />)}/>  
                    <ResultsTable result={albumsList.map((a,i) => <Album key={i} {...a} />)}/>  
                </div>
            </div>
        )
    }
}

export default SearchForm;
