import React, {Component} from 'react';

class Modal extends Component {

    render() {
        const {visual, click} = this.props;
        return (
            <aside className="modal" hidden={visual}>
                <div className="modal-wrapper">
                    <button onClick={click} className="modal-close" id="modal-close" value="Fermer la modal">X</button>
                    <p>test</p>
                    <p>test</p>
                </div>
            </aside>  
        )
    }
}

export default Modal;
