import React, {Component} from 'react';
import {sendSearchByArtist} from '../api/musicBrainzAPI'; 
import {sendSearchByAlbum} from '../api/musicBrainzAPI'; 
import {sendSearchByTitle} from '../api/musicBrainzAPI'; 
import Response from './Response';
import ResultsTable from './ResultsTable';

class SearchForm extends Component {

    constructor(props){
        super(props);
        this.state = {
          "responsesList" : [],
        }
      };

    submit = (ev) => {
        ev.preventDefault();
        const {addSearch, addSelect} = ev.target;
        if(addSelect.value === "artist"){
            sendSearchByArtist(addSearch.value,this.addResponseByArtist);
        }else if(addSelect.value === "release"){
            sendSearchByAlbum(addSearch.value,this.addResponseByAlbum);
        }else if(addSelect.value === "recording"){
            sendSearchByTitle(addSearch.value, this.addResponseByTitle);
        }else {
            //pour everything
        }

    };

    addResponseByArtist = (artists) => {
        const {responsesList} = this.state;

        artists.recordings.map(a => 
            responsesList.push({
                "artistName":a["artist-credit"][0].name,
                "titleName": a.title,
                "albumName": a["releases"][0].title,
        }));

        this.setState({
            "responsesList": responsesList,
        })
    };

    addResponseByAlbum = (albums) => {
        const {responsesList} = this.state;

        albums.releases.map(a => 
            responsesList.push({
                "albumName": a.title,
        }));

        this.setState({
            "responsesList": responsesList,
        })
    };

    addResponseByTitle = (titles) => {
        const {responsesList} = this.state;

        titles.recordings.map(a => 
            responsesList.push({
                "artistName":a["artist-credit"][0].name,
                "titleName": a.title,
                "albumName": a["releases"][0].title,
        }));

        this.setState({
            "responsesList": responsesList,
        })
    };

    render() {
        const {responsesList} = this.state;
        return (
            <div>
                <form onSubmit={this.submit}>
                        <label >Votre recherche : </label>
                        <input type="text" placeholder="Votre recherche par Artiste, Album, Titre ou dans tout" name="addSearch" />
                        <select name="addSelect">
                            <option value="everything">Tout</option>
                            <option value="artist">Artiste</option>
                            <option value="release">Album</option>
                            <option value="recording">Titre</option>
                        </select>
                        <input type="submit" value="Rechercher"/>
                </form>

                <ResultsTable result={responsesList.map((r,i) => <Response key={i} {...r} />)}/>  
            </div>
        )
    }
}

export default SearchForm;
