function sendSearch(selectValue, searchValue, offset, addResponse){
    const request = new XMLHttpRequest();
    if(selectValue === "everything"){
        request.open("GET", 'https://musicbrainz.org/ws/2/recording?query="' + searchValue +'"&fmt=json&limit=100&offset=' + offset, true);
    }else{
        request.open("GET", 'https://musicbrainz.org/ws/2/recording?query=' + selectValue + ':"' + searchValue +'"&fmt=json&limit=100&offset=' + offset, true);
    }
    request.addEventListener("readystatechange",function(){
        if(request.readyState === XMLHttpRequest.DONE && request.status === 200){
            const response = JSON.parse(request.responseText);
            addResponse(response);
            // console.log(response);
            // console.log(response.recordings[0].title);
            // console.log(response.recordings[0]["artist-credit"][0].name);
            // console.log(response.recordings[0]["releases"][0].title);

        }
    });
    request.send();
}

// export {sendSearchByArtist, sendSearchByAlbum, sendSearchByTitle};
export {sendSearch};
