import React, {Component} from 'react';
import './App.css';
import {recupArtist} from '../src/api/musicBrainzAPI';
import Artist from "./components/Artist";


class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      "artistsList" : [],
    }
  };

  componentDidMount() {
    recupArtist(this.addArtist) 
  }

  addArtist = (artists) => {
    const {artistsList} = this.state;

    artists.map(a => 
      artistsList.push({
        "name": a.name,
    }));

    this.setState({
        "artistsList": artistsList,
    })
  };

  render() {
    const {artistsList} = this.state;
      return (
        <div className="App">
          {artistsList.map((t,i) => <Artist key={i} {...t} />)}
        </div>
      )
  }
}

export default App;
