import React, {Component} from 'react';
import {sendSearchArtist} from '../api/musicBrainzAPI'; 
import {sendSearchAlbum} from '../api/musicBrainzAPI'; 
import {sendSearchTitle} from '../api/musicBrainzAPI'; 

class SearchForm extends Component {

    // constructor(props){
    //     super(props);
    //     this.state = {
    //       "artistsList" : [],
    //     }
    //   };

    submit = (ev) => {
        ev.preventDefault();
        const {addSearch, addSelect} = ev.target;
        if(addSelect.value === "artist"){
            sendSearchArtist(addSearch.value,this.addArtists);
        }else if(addSelect.value === "album"){
            sendSearchAlbum(addSearch.value);
        }else if(addSelect.value === "title"){
            sendSearchTitle(addSearch.value);
        }else {
            //pour everything
        }

    };

    // addArtists = (artists) => {
