import React, {Component} from 'react';
import './App.css';
import SearchForm from './components/SearchForm';
import Modal from './components/Modal';
import Response from './components/Response';
import ResultsTable from './components/ResultsTable';
import {sendSearch} from './api/musicBrainzAPI'; 


class App extends Component {

  constructor(props){
      super(props);
      this.state = {
        "responsesList" : [],
        "addSearch": "",
        "addSelect": "",
        'seeMore': false,
        'visuResult': true,
        'visualModal': true,
      }
      this.offset = 0;
    };

  submit = (ev) => {
      ev.preventDefault();

      this.setState({
          "responsesList": [],
          "addSearch": "",
          "addSelect": "",
      })
      this.offset = 0;

      const {addSearch, addSelect} = ev.target;

      this.setState({
          "addSearch": addSearch.value,
          "addSelect": addSelect.value,
      })

      sendSearch(addSelect.value, addSearch.value, this.offset, this.addResponse);
  };

  click = () => {
      const {addSearch, addSelect} = this.state;
      this.offset += 100;
      sendSearch(addSelect, addSearch, this.offset, this.addResponse);

  };

  addResponse = (response) => {
      const {responsesList} = this.state;

      response.recordings.map((a) => 
          responsesList.push({
              "artistName":a["artist-credit"]?.[0].name,
              "titleName": a?.title,
              "albumName": a["releases"]?.[0].title,
      }));

      this.setState({
          "responsesList": responsesList,
      })

      // this.props.test(responsesList);

      //pour gérer l'apparission ou non du bouton "voir plus"
      if(this.offset+100 < response.count){
          this.setState({
              "seeMore": true,
          });
      }else{
          this.setState({
              "seeMore": false,
          }); 
      } 

      //pour gérer l'apparission ou non du tableau des résultats ou d'un texte
      if(response.count != 0){
        this.setState({
          "visuResult": true,
        }); 
      }else{
        this.setState({
          "visuResult": false,
        }); 
      }

  };

  clickModal = () => {
    const {visualModal} = this.state;
    this.setState({
      "visualModal": !visualModal,
    }); 
  };

  render() {
      const {responsesList, seeMore, visualModal, visuResult} = this.state;
      return (
          <div className="App">
              <form onSubmit={this.submit}>
                      <label >Votre recherche : </label>
                      <input type="text" placeholder="Votre recherche par Artiste, Album, Titre ou dans tout" name="addSearch" />
                      <select name="addSelect">
                          <option value="everything">Tout</option>
                          <option value="artist">Artiste</option>
                          <option value="release">Album</option>
                          <option value="recording">Titre</option>
                      </select>
                      <input type="submit" value="Rechercher"/>
              </form>

              {visuResult && <ResultsTable result={responsesList.map((r,i) => <Response key={i} {...r} numRslt={i+1} click={this.clickModal}/>)}/> }

              {!visuResult && <p>Aucun résultat pour votre recherche</p>}
              {seeMore && <button onClick={this.click}>Voir plus</button> }
              {/* <aside className="modal" hidden={visualModal}>
                <div className="modal-wrapper">
                    <button onClick={this.clickModal} className="modal-close" id="modal-close" value="Fermer la modal">X</button>
                    <p>test</p>
                    <p>test</p>
                </div>
              </aside>   */}
              <Modal visual={visualModal} click={this.clickModal}/> 
          </div>
      )
  }
}

export default App;
