import React, {Component} from 'react';

class Modal extends Component {

    click = () => {
        this.props.visual = "hidden"
    };

    render() {
        const {visual} = this.props;
        return (
            <aside className="modal" id="container-modal" hidden={visual}>
                <div className="modal-wrapper" id="modal">
                    <button onClick={this.click} className="modal-close" id="modal-close" value="Fermer la modal">X</button>
                    <p>test</p>
                    <p>test</p>
                </div>
            </aside>  
        )
    }
}

export default Modal;
