import React, {Component} from 'react';

class Cover extends Component {

    constructor(props){
        super(props);
    };

    render() {
        const {url} = this.props;
        const {cover} = this.state;
        return (
            <span>
                {cover && <img alt="" src={url}/>}
                {!cover && <p>Pas de cover disponible</p>}
            </span>
        )
    }
}

export default Cover;
