function recupArtist(addArtist){
    const requestArtist = new XMLHttpRequest();
    requestArtist.open("GET", "https://musicbrainz.org/ws/2/artist", true);
    requestArtist.addEventListener("readystatechange",function(){
        if(requestArtist.readyState === XMLHttpRequest.DONE && requestArtist.status === 200){
            const requestArtist = JSON.parse(requestArtist.responseText);
            addArtist(requestArtist);
        }
    });
    requestArtist.send();
}

export {recupArtist};
