class App extends Component {

  submit = (ev) => {
    ev.preventDefault();
    const {addSelect, addSearch} = ev.target;
    const searchValue = addSearch.value; 
    function sendSearch(searchValue){
      const request = new XMLHttpRequest();
      request.open("GET", "https://musicbrainz.org/ws/2/artist?query=" + searchValue +"&fmt=json", true);
      request.addEventListener("readystatechange",function(){
          if(request.readyState === XMLHttpRequest.DONE && request.status === 200){
              const response = JSON.parse(request.responseText);
              addArtists(response);
          }
      });
      request.send();
    }

};



  render() {

      return (
        <div className="App">
          <form onSubmit={this.submit}>
                    <label >Votre recherche : </label>
                    <input type="text" placeholder="Votre recherche par Artiste, Album, Titre ou dans tout" name="addSearch" />
                    <select name="addSelect">
                        <option value="everything">Tout</option>
                        <option value="artist">Artiste</option>
                        <option value="album">Album</option>
                        <option value="title">Titre</option>
                    </select>
                    <input type="submit" value="Rechercher"/>
            </form>
          {/* <ResultsTable/> */}
        </div>
      )
  }
}

export default App;
