function sendSearchArtist(searchValue, addArtists){
    const requestArtist = new XMLHttpRequest();
    requestArtist.open("GET", "https://musicbrainz.org/ws/2/artist?query=" + searchValue +"&fmt=json", true);
    requestArtist.addEventListener("readystatechange",function(){
        if(requestArtist.readyState === XMLHttpRequest.DONE && requestArtist.status === 200){
            const responseArtist = JSON.parse(requestArtist.responseText);
            // addArtists(responseArtist);
            console.log(responseArtist.artists[0].name);
        }
    });
    requestArtist.send();
}

function sendSearchAlbum(searchValue, addAlbums){
    const requestAlbum = new XMLHttpRequest();
    requestAlbum.open("GET", "https://musicbrainz.org/ws/2/release?query=" + searchValue + "&fmt=json", true);
    requestAlbum.addEventListener("readystatechange",function(){
        if(requestAlbum.readyState === XMLHttpRequest.DONE && requestAlbum.status === 200){
            const responseAlbum = JSON.parse(requestAlbum.responseText);
            addAlbums(responseAlbum);
        }
    });
    requestAlbum.send();
}

function sendSearchTitle(searchValue, addTitles){
    const requestTitle = new XMLHttpRequest();
    requestTitle.open("GET", "https://musicbrainz.org/ws/2/recording?query=" + searchValue + "&fmt=json", true);
    requestTitle.addEventListener("readystatechange",function(){
        if(requestTitle.readyState === XMLHttpRequest.DONE && requestTitle.status === 200){
            const responseTitle = JSON.parse(requestTitle.responseText);
            addTitles(responseTitle);
        }
    });
    requestTitle.send();
}

export {sendSearchArtist, sendSearchAlbum, sendSearchTitle};
