import React, {Component} from 'react';
import {sendSearch} from '../api/musicBrainzAPI'; 
import Response from './Response';
import ResultsTable from './ResultsTable';

class SearchForm extends Component {

    constructor(props){
        super(props);
        this.state = {
          "responsesList" : [],
          "addSearch": "",
          "addSelect": "",
        }
      };

    submit = (ev) => {
        ev.preventDefault();

        this.setState({
            "responsesList": [],
            "addSearch": "",
            "addSelect": "",
        })

        const {addSearch, addSelect} = ev.target;

        this.setState({
            "addSearch": addSearch.value,
            "addSelect": addSelect.value,
        })

        sendSearch(addSelect.value, addSearch.value, 0, this.addResponse);
    };

    click = () => {

        sendSearch(this.state.addSelect, this.state.addSelect, 100, this.addResponse);
    };

    addResponse = (response) => {
        const {responsesList} = this.state;

        response.recordings.map((a,i) => 
            responsesList.push({
                "id": i+1,
                "artistName":a["artist-credit"]?.[0].name,
                "titleName": a?.title,
                "albumName": a["releases"]?.[0].title,
        }));

        this.setState({
            "responsesList": responsesList,
        })
    };


    render() {
        const {responsesList} = this.state;
        return (
            <div>
                <form onSubmit={this.submit}>
                        <label >Votre recherche : </label>
                        <input type="text" placeholder="Votre recherche par Artiste, Album, Titre ou dans tout" name="addSearch" />
                        <select name="addSelect">
                            <option value="everything">Tout</option>
                            <option value="artist">Artiste</option>
                            <option value="release">Album</option>
                            <option value="recording">Titre</option>
                        </select>
                        <input type="submit" value="Rechercher"/>
                </form>

                <ResultsTable result={responsesList.map((r,i) => <Response key={i} {...r} />)}/>  
                <button onClick={this.click}>Voir plus</button>
            </div>
        )
    }
}

export default SearchForm;
