import React, {Component} from 'react';
import './App.css';
// import SearchForm from "./components/SearchForm";
import ResultsTable from "./components/ResultsTable";


class App extends Component {

  submit = (ev) => {
    ev.preventDefault();
    const {addSearch, addSelect} = ev.target;
    if(addSelect.value === "artist"){
        sendSearchArtist(addSearch.value,this.addArtists);
    }else if(addSelect.value === "album"){
        sendSearchAlbum(addSearch.value);
    }else if(addSelect.value === "title"){
        sendSearchTitle(addSearch.value);
    }else {
        //pour everything
    }

};

  render() {

      return (
        <div className="App">
          <form onSubmit={this.submit}>
                    <label >Votre recherche : </label>
                    <input type="text" placeholder="Votre recherche par Artiste, Album, Titre ou dans tout" name="addSearch" />
                    <select name="addSelect">
                        <option value="everything">Tout</option>
                        <option value="artist">Artiste</option>
                        <option value="album">Album</option>
                        <option value="title">Titre</option>
                    </select>
                    <input type="submit" value="Rechercher"/>
            </form>
          <ResultsTable/>
        </div>
      )
  }
}

export default App;
