function sendSearch(selectValue, searchValue, addResponse){
    const requestArtist = new XMLHttpRequest();
    if(selectValue === "everything"){
        requestArtist.open("GET", 'https://musicbrainz.org/ws/2/recording?query="' + searchValue +'"&limit=100&fmt=json', true);
    }else{
        requestArtist.open("GET", 'https://musicbrainz.org/ws/2/recording?query=' + selectValue + ':"' + searchValue +'"&fmt=json', true);
    }
    requestArtist.addEventListener("readystatechange",function(){
        if(requestArtist.readyState === XMLHttpRequest.DONE && requestArtist.status === 200){
            const responseArtist = JSON.parse(requestArtist.responseText);
            addResponse(responseArtist);
            // console.log(responseArtist.recordings[0].title);
            // console.log(responseArtist.recordings[0]["artist-credit"][0].name);
            // console.log(responseArtist.recordings[0]["releases"][0].title);

        }
    });
    requestArtist.send();
}

// export {sendSearchByArtist, sendSearchByAlbum, sendSearchByTitle};
export {sendSearch};
