import React, {Component} from 'react';

class Cover extends Component {

    render() {
        const {url, cover} = this.props;
        return (
            <span>
                {cover && <img alt="" src={url}/>}
                {!cover && <p>Pas de cover disponible</p>}
            </span>
        )
    }
}

export default Cover;
